import React, {useState, useEffect} from 'react';
import {Link} from 'react-router-dom';
import queryString from 'query-string';
import axios from 'axios';
import 'bootstrap/dist/css/bootstrap.min.css';

function deleteTask (id) {
  if (window.confirm('Are you sure')){
      fetch('https://martyxtodolist.herokuapp.com/task/'+id, {
        method:'DELETE',
        header:{'Accept':'application/json',
        'Content-Type':'application/json'
          }
      } )
    }
  }  



export default class UserTasks extends React.Component {
 



  constructor(props){
super(props)

 this.state = {tasks : []};
  }

  
  
  componentDidMount() {
    axios.get(`https://martyxtodolist.herokuapp.com/task/usertask/`+this.props.match.params.id)
      .then(res => {
        const tasks = res.data;
        this.setState( {tasks});
      })
  }



  render() {
    let id = this.props.match.params.id;

    let url = this.props.location.search;
    let params = queryString.parse(url);
    console.log(params);

   
      return (
        <div class="container">
          <form onSubmit={this.handleSubmit}>
        <div class="row">
          <div class="col ">
            <div class="card">  
              <div class="card-body">
              <h4 className= "text-center">Tasks List of User with ID: {id}</h4>
                   <div class="table-responsive">
                     <table className="table table-striped">
                       <thead>
                         <tr>
                                <th scope="col"><strong>Id</strong></th>
                                <th scope="col"><strong>UserId</strong></th>
                                <th scope="col"><strong>Name</strong></th>
                                <th scope="col"><strong>Status</strong></th>
                                <th scope="col"><strong>Category</strong></th>
                                <th scope="col"><strong>Description</strong></th>
                                <th scope="col"><strong>Created</strong></th>
                                <th scope="col-5"></th>
                               
                                            
                                     
                  </tr>
             
                  </thead>
                {this.state.tasks.map(task => (
                   
                  <tbody>
                  <tr key={task.id}>
                   <th scope="row"><Link to={`/user/${task.id}`}>{task.id}</Link></th>
                   <td>{task.userId}</td>
                   <td>{task.name}</td>
                   <td>{task.status}</td>
                   <td>{task.category}</td>
                   <td>{task.description}</td>
                   <td>{task.createdAt}</td>
                   <td> 
            
             <Link to={`/user/${task.userId}/usertasks/edit/${task.id}`}><a  class="btn btn-sm btn-primary ">Edit Task<i class="far fa-edit ml-1"></i></a> </Link>
             <a onClick={()=> deleteTask(task.id)} class="btn btn-sm btn-danger ">Delete task<i class="far fa-edit"></i></a></td> 
          

                    
               </tr> 
               
               
              </tbody>

))}
           </table>
           
           <div class="col-3 text-left">
           <Link to={`/user/${id}/usertasks/create`}><a  class="btn btn-sm btn-primary">Create new Task <i class="far fa-edit ml-1"></i></a> </Link>
          
           </div>
          
                          </div>
                       </div>
                    </div>
                   </div>
                 </div>
                 </form>
               </div>
               
      )
    }
  }


      
 


 
